mapper.select:
SELECT ID, PROCESSO_ID, APRESENTACAO, NATUREZA, NOME FROM MARCA
WHERE PROCESSO_ID = #{processo.id}
AND APRESENTACAO = #{apresentacao}
AND NATUREZA = #{natureza}

mapper.update:
UPDATE MARCA
SET PROCESSO_ID = #{processo.id},
APRESENTACAO = #{apresentacao},
NATUREZA = #{natureza},
NOME = #{nome}
WHERE ID = #{id}
